package com.baidu.camare.helloopengl.vedio;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import com.baidu.camare.helloopengl.R;public class RecordVedioActivity extends Activity implements View.OnClickListener {    private Button btn_record, btn_video;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_vedio);        btn_record = (Button) findViewById(R.id.btn_record);        btn_video = (Button) findViewById(R.id.btn_video);        btn_record.setOnClickListener(this);        btn_video.setOnClickListener(this);    }    @Override    public void onClick(View v) {        Intent intent;        switch (v.getId()) {            case R.id.btn_record:                intent = new Intent(RecordVedioActivity.this, RecordActivity.class);                startActivity(intent);                break;            case R.id.btn_video:                intent = new Intent(RecordVedioActivity.this, VideoActivity.class);                startActivity(intent);                break;            default:                break;        }    }}